import { Injectable } from '@angular/core';
import { SocialAuthService, SocialUser } from "@abacritt/angularx-social-login";
import { GoogleLoginProvider } from "@abacritt/angularx-social-login";
import { Subject } from 'rxjs';
import { HttpClient } from '@angular/common/http';
// import { EnvironmentUrlService } from './path/to/environment-url.service';


@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = 'http://localhost:5115/api/Account/PostUser';
  user:any
  private authChangeSub = new Subject<boolean>();
private extAuthChangeSub = new Subject<SocialUser>();
public authChanged = this.authChangeSub.asObservable();
public extAuthChanged = this.extAuthChangeSub.asObservable();
constructor(private http: HttpClient,
  //  private envUrl: EnvironmentUrlService, 
  // private jwtHelper: JwtHelperService,
   private externalAuthService: SocialAuthService) { 
    this.externalAuthService.authState.subscribe((user) => {
      console.log(user)
      this.user = user
      this.extAuthChangeSub.next(user);
    })
  }
  //'400690855047-gkqnu3ioohb9vbc0pevpoa5h5eis6oc6.apps.googleusercontent.com'

  public signInWithGoogle = ()=> {
    this.externalAuthService.signIn(GoogleLoginProvider.PROVIDER_ID);
  }
  sendUserData(user: any) {
    console.log(user)
    return this.http.post(this.apiUrl, user).pipe(catchError((err: { message: any; }) => {
      return throwError(() => err.message || "server error");
    }));;
  }
  sendUserDataToApi(userData: any): void {
    const user = {
      id: userData.id,
      email: userData.email,
      name: userData.name,
      imageUrl: userData.imageUrl,
    };
  }
  public signOutExternal = () => {
    this.externalAuthService.signOut();
  }
}
